const tileSize = 32;
const obstacles = [];

let selection = 0;
let obsIdCounter = 0;
var t;

function menu() {
    t = setInterval(menuStep, 100);
}

function menuStep() {
    //requestAnimationFrame(menuStep);
    ctx.fillStyle = "black";
    ctx.fillRect(0, 0, canvasWidth, canvasHeight);
    
    ctx.font = "36px 'Press Start 2P'"
    ctx.fillStyle = "green";
    ctx.fillText("Bienvenue sur mon jeu!", 250, 200);

    ctx.font = "24px 'Press Start 2P'"
    ctx.fillStyle = "darkgreen";
    if (selection === 0) {
        ctx.fillStyle = "red";
        ctx.beginPath();
        ctx.moveTo(470, 270);
        ctx.lineTo(500, 285);
        ctx.lineTo(470, 300);
        ctx.fill();
    }
    ctx.fillText("COMMENCER", 540, 300);

    ctx.font = "24px 'Press Start 2P'"
    ctx.fillStyle = "darkgreen";
    if (selection === 1) {
        ctx.fillStyle = "red";
        ctx.beginPath();
        ctx.moveTo(460, 370);
        ctx.lineTo(490, 385);
        ctx.lineTo(460, 400);
        ctx.fill();
    }
    ctx.fillText("PARAMETRES", 530, 400);

    ctx.font = "24px 'Press Start 2P'"
    ctx.fillStyle = "darkgreen";
    if (selection === 2) {
        ctx.fillStyle = "red";
        ctx.beginPath();
        ctx.moveTo(500, 470);
        ctx.lineTo(530, 485);
        ctx.lineTo(500, 500);
        ctx.fill();
    }
    ctx.fillText("QUITTER", 560, 500);

    menuKeys();
}

function menuKeys() {
    if (upKey) {
        if (selection > 0) { selection--; }
    } else if (downKey) {
        if (selection < 2) { selection++; }
    } else if (selection === 0 && enterKey) {
        gameStart();
    }
}

